def get_distance(genotype1, genotype2):
    diffs = 0
    length = len(genotype1)
    assert len(genotype1) == len(genotype2)
    for chr_a, chr_b in zip(genotype1, genotype2):
        if chr_a != chr_b:
            diffs += 1
    return diffs / float(length)

